import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent {
}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Welcome to Codflix!</title>
  <style>
    body {
      font-family: Arial, sans-serif;
      background-color: #f5f5f5;
      margin: 0;
      padding: 0;
    }

    .container {
      max-width: 800px;
      margin: 50px auto;
      padding: 0 20px;
    }

    h1 {
      font-size: 36px;
      color: #333;
      margin-bottom: 20px;
    }

    p {
      font-size: 18px;
      line-height: 1.6;
      color: #555;
      margin-bottom: 20px;
    }

    ul {
      list-style-type: none;
      padding-left: 0;
    }

    li {
      margin-bottom: 10px;
    }

    footer {
      background-color: #333;
      color: #fff;
      padding: 20px 0;
      text-align: center;
    }

      footer p {
        margin: 0;
      }

        footer p a {
          color: #fff;
          text-decoration: none;
        }

          footer p a:hover {
            text-decoration: underline;
          }
  </style>
</head>
<body>
  <div class="container">
    <h1>Welcome to Codflix!</h1>
    <p>Discover a world of entertainment with Codflix - your ultimate destination for streaming movies and TV shows.</p>
    <ul>
      <li>Explore a vast library of the latest blockbuster movies and popular TV series.</li>
      <li>Experience seamless streaming on any device, anytime, anywhere.</li>
      <li>Unlock exclusive content and personalized recommendations tailored just for you.</li>
    </ul>
    <p>Start your cinematic journey with Codflix today!</p>
  </div>
  <footer>
    <p>&copy; 2024 Codflix. All rights reserved. Made with ❤️</p>
  </footer>
</body>
</html>
